<?php
 use Tygh\Registry; use Tygh\Enum\ProductFeatures; defined('BOOTSTRAP') or die('Access denied'); function fn_settings_variants_addons_sd_facebook_pixel_brand_feature() { $empty_feature = array( '' => __('empty'), ); $features_list = db_get_hash_single_array( 'SELECT fd.description, f.feature_id FROM ?:product_features as f LEFT JOIN ?:product_features_descriptions as fd ON f.feature_id = fd.feature_id WHERE fd.lang_code = ?s AND f.feature_type != ?s', array('feature_id', 'description'), DESCR_SL, ProductFeatures::GROUP ); $features_list = $empty_feature + $features_list; return $features_list; }